INSERT INTO `wp_posts` VALUES ('47', '1', '2015-02-05 17:47:24', '2015-02-05 17:47:24', 'Thân chào các bạn.<br />\r\n<br />\r\nKì thi JLIT vào ngày thứ 7 tuần tới đã đến gần rồi.<br />\r\nNhững phần thưởng hấp dẫn như iPad, TV hoặc phiếu mua hàng AEON đang chờ các bạn đấy.<br />\r\nNgoài ra, hiện nay trong ngành công nghiệp IT, các ứng viên IT thông thạo tiếng Nhật ngày càng cần thiết.<br />\r\nĐây chính là cơ hội để các bạn chứng tỏ cũng như nâng cao kỹ năng của mình cho thành công trong tương lai.<br />\r\nCâu hỏi<br />\r\n<br />\r\nCác đối tượng vật lý như thiết bị ngoại vi hay mạch điện tử cấu tạo nên máy vi tính gọi là gì.<br />\r\n1. ハード　　2. ソフトウェア　　3. アプリ　　4. コンテンツ<br />\r\n<br />\r\nThao tác tìm kiếm sai sót trong trương trình máy tính và loại bỏ.<br />\r\n1. バグ　　　2. ハッシュ　　　3. ダウン　　　4. デバック<br />\r\n<br />\r\nĐáp án kỳ trước<br />\r\n2.イーコマース', 'Kì thi JLIT vào ngày thứ 7 tuần tới đã đến gần rồi.', '', 'publish', 'open', 'open', '', 'sub-news-4', '', '', '2015-02-05 10:34:32', '2015-02-05 10:34:32', '', '0', 'http://localhost:8060/?p=47', '0', 'post', '', '0');
